DROP TABLE IF EXISTS `GameTips`; 
CREATE TABLE `GameTips` ( `ID` INT NOT NULL DEFAULT '0', `Text_Lang_enUS` TEXT NULL, `Text_Lang_enGB` TEXT NULL, `Text_Lang_koKR` TEXT NULL, `Text_Lang_frFR` TEXT NULL, `Text_Lang_deDE` TEXT NULL, `Text_Lang_enCN` TEXT NULL, `Text_Lang_zhCN` TEXT NULL, `Text_Lang_enTW` TEXT NULL, `Text_Lang_zhTW` TEXT NULL, `Text_Lang_esES` TEXT NULL, `Text_Lang_esMX` TEXT NULL, `Text_Lang_ruRU` TEXT NULL, `Text_Lang_ptPT` TEXT NULL, `Text_Lang_ptBR` TEXT NULL, `Text_Lang_itIT` TEXT NULL, `Text_Lang_Unk` TEXT NULL, `Text_Lang_Mask` INT UNSIGNED NOT NULL DEFAULT '0', PRIMARY KEY (`ID`)) ENGINE=InnoDB DEFAULT CHARSET=utf8; 
INSERT INTO `GameTips` VALUES (396,"|cffffd100Tip:|r Nearby questgivers that are awaiting your return are shown as a question mark on your mini-map.
","","","","","","","","","","","","","","","",16712190),
(201,"|cffffd100Tip:|r Your spell casting can be cancelled by moving, jumping or hitting the escape key.

","","","","","","","","","","","","","","","",16712190),
(221,"|cffffd100Tip:|r Clicking on a player name in the chat window lets you send a private message to them.
","","","","","","","","","","","","","","","",16712190),
(258,"|cffffd100Tip:|r If you <Shift>Click on a player name in the chat window it tells you additional information about them.
","","","","","","","","","","","","","","","",16712190),
(222,"|cffffd100Tip:|r You can <Control>Click on an item to see how you would look wearing that item.
","","","","","","","","","","","","","","","",16712190),
(223,"|cffffd100Tip:|r An item with its name in gray is a poor quality item and generally can be sold to a vendor.
","","","","","","","","","","","","","","","",16712190),
(224,"|cffffd100Tip:|r An item with its name in white is useful to players in some way and can be used or sold at the auction house.
","","","","","","","","","","","","","","","",16712190),
(225,"|cffffd100Tip:|r You can send mail to other players or even to your other characters from any mailbox in game.
","","","","","","","","","","","","","","","",16712190),
(226,"|cffffd100Tip:|r You can <Shift>Click on an item to place an item link into a chat message.
","","","","","","","","","","","","","","","",16712190),
(384,"|cffffd100Tip:|r You can remove a friendly spell enhancement on yourself by right-clicking on the spell effect icon.","","","","","","","","","","","","","","","",16712190),
(282,"|cffffd100Tip:|r When you learn a profession or secondary skill the button that allows you to perform that skill is found in the general tab of your spellbook.","","","","","","","","","","","","","","","",16712190),
(227,"|cffffd100Tip:|r All of your action bars can have their hotkeys remapped in the key bindings interface.
","","","","","","","","","","","","","","","",16712190),
(229,"|cffffd100Tip:|r On your character sheet is a reputation tab that tells you your status with different groups.
","","","","","","","","","","","","","","","",16712190),
(263,"|cffffd100Tip:|r You can use the Tab key to select nearby enemies in front of you.
","","","","","","","","","","","","","","","",16712190),
(321,"|cffffd100Tip:|r If you are having trouble finding something in a capital city, try asking a guard for directions.
","","","","","","","","","","","","","","","",16712190),
(476,"|cffffd100Tip:|r You can use the V key to show enemy name plates above the enemy's head.  Name plates may help you keep track of enemy health or spell casting, or may make it easier to quickly change targets.","","","","","","","","","","","","","","","",16712190),
(470,"|cffffd100Tip:|r You can track many things, such as low-level quests or vendors by clicking on the magnifying glass near your mini map.","","","","","","","","","","","","","","","",16712190),
(230,"|cffffd100Tip:|r You can perform many fun actions with the emote system, for instance you can type /dance to dance.
","","","","","","","","","","","","","","","",16712190),
(387,"|cffffd100Tip:|r A Blizzard employee will NEVER ask for your password.","","","","","","","","","","","","","","","",16712190),
(389,"|cffffd100Tip:|r You can only know two professions at a time, but you can learn all of the secondary skills (fishing, cooking and first-aid).","","","","","","","","","","","","","","","",16712190),
(231,"|cffffd100Tip:|r The interface options menu <ESC> has lots of ways to customize your game play.
","","","","","","","","","","","","","","","",16712190),
(232,"|cffffd100Tip:|r You can turn on slow scrolling of quest text in the interface options menu.

","","","","","","","","","","","","","","","",16712190),
(233,"|cffffd100Tip:|r Spend your talent points carefully as once your talents are chosen, you must spend gold to unlearn them.
","","","","","","","","","","","","","","","",16712190),
(234,"|cffffd100Tip:|r A mail icon next to the minimap means you have new mail.  Visit a mailbox to retrieve it.
","","","","","","","","","","","","","","","",16712190),
(235,"|cffffd100Tip:|r You can add additional action bars to your game interface from the interface options menu.
","","","","","","","","","","","","","","","",16712190),
(236,"|cffffd100Tip:|r If you hold down <Shift> while right-clicking on a target to loot, you will automatically loot all items on the target.
","","","","","","","","","","","","","","","",16712190),
(401,"|cffffd100Tip:|r Your character can eat and drink at the same time.","","","","","","","","","","","","","","","",16712190),
(237,"|cffffd100Tip:|r If you enjoyed playing with someone from your realm, put them on your friends list!
","","","","","","","","","","","","","","","",16712190),
(406,"|cffffd100Tip:|r Use the Dungeon Finder tool to quickly travel to dungeons or to find more players for your dungeon group.


","","","","","","","","","","","","","","","",16712190),
(239,"|cffffd100Tip:|r There are a number of different loot options when in a group. The group leader can right-click their own portrait to change the options.
","","","","","","","","","","","","","","","",16712190),
(240,"|cffffd100Tip:|r If you never want to hear from another player again, you can /ignore them. You can always change your mind later.
","","","","","","","","","","","","","","","",16712190),
(241,"|cffffd100Tip:|r You can target members of your party with the function keys.  F1 targets you; F2 targets the second party member.
","","","","","","","","","","","","","","","",16712190),
(242,"|cffffd100Tip:|r Being polite while in a group with others will get you invited back!","","","","","","","","","","","","","","","",16712190),
(341,"|cffffd100Tip:|r Remember to take all things in moderation (even World of Warcraft!)","","","","","","","","","","","","","","","",16712190),
(243,"|cffffd100Tip:|r You can click on a faction in the reputation pane to get additional information and options about that faction.

","","","","","","","","","","","","","","","",16712190),
(244,"|cffffd100Tip:|r A monster with a silver dragon around its portrait is a rare monster with better than average treasure.
","","","","","","","","","","","","","","","",16712190),
(245,"|cffffd100Tip:|r If you mouse over a chat pane it will become visible and you can Right Click on the chat pane tab for options.
","","","","","","","","","","","","","","","",16712190),
(411,"|cffffd100Tip:|r Sharing an account with someone else can compromise its security.
","","","","","","","","","","","","","","","",16712190),
(246,"|cffffd100Tip:|r You can display the duration of beneficial spells on you from the interface options menu.
","","","","","","","","","","","","","","","",16712190),
(247,"|cffffd100Tip:|r You can lock your action bar so you don't accidentally move spells. This is done using the interface options menu.
","","","","","","","","","","","","","","","",16712190),
(256,"|cffffd100Tip:|r You can change which buffs or debuffs are displayed on yourself or targets in order to get the information you need.
","","","","","","","","","","","","","","","",16712190),
(248,"|cffffd100Tip:|r You can cast a spell on yourself without deselecting your current target by holding down <Alt> while pressing your hotkey.
","","","","","","","","","","","","","","","",16712190),
(398,"|cffffd100Tip:|r Ensure that all party members are on the same stage of an escort quest before beginning it.","","","","","","","","","","","","","","","",16712190),
(405,"|cffffd100Tip:|r You're much less likely to encounter wandering monsters while following a road.","","","","","","","","","","","","","","","",16712190),
(250,"|cffffd100Tip:|r Killing guards gives no honor.","","","","","","","","","","","","","","","",16712190),
(254,"|cffffd100Tip:|r You can hide your interface with <Alt>Z and take screenshots with <Print Screen>.
","","","","","","","","","","","","","","","",16712190),
(251,"|cffffd100Tip:|r Typing /macro will bring up the interface to create macros.
","","","","","","","","","","","","","","","",16712190),
(252,"|cffffd100Tip:|r Enemy players whose names appear in gray are much lower level than you are and will not give honor when killed.
","","","","","","","","","","","","","","","",16712190),
(253,"|cffffd100Tip:|r From the Raid UI you can drag a player to the game field to see their status or drag a class icon to see all members of that class.

","","","","","","","","","","","","","","","",16712190),
(261,"|cffffd100Tip:|r  A blue question mark above a quest giver means the quest is repeatable. 
","","","","","","","","","","","","","","","",16712190),
(262,"|cffffd100Tip:|r Use the assist button (F key) while targeting another player, and it will target the same target as that player.
","","","","","","","","","","","","","","","",16712190),
(281,"|cffffd100Tip:|r <Shift>Clicking on an item being sold by a vendor will let you select how many of that item you wish to purchase.","","","","","","","","","","","","","","","",16712190),
(402,"|cffffd100Tip:|r Playing in a battleground on its holiday weekend increases your honor gained.
","","","","","","","","","","","","","","","",16712190),
(400,"|cffffd100Tip:|r If you are having trouble fishing in an area, try attaching a lure to your fishing pole.","","","","","","","","","","","","","","","",16712190),
(380,"|cffffd100Tip:|r You can view messages you previously sent in chat by pressing <Alt> and the up arrow key.","","","","","","","","","","","","","","","",16712190),
(381,"|cffffd100Tip:|r You can Shift-Click on an item stack to split it into smaller stacks.","","","","","","","","","","","","","","","",16712190),
(382,"|cffffd100Tip:|r Pressing both mouse buttons simultaneously will make your character run.","","","","","","","","","","","","","","","",16712190),
(383,"|cffffd100Tip:|r When replying to a tell from a player (Default 'R'), the <TAB> key cycles through people you have recently replied to.","","","","","","","","","","","","","","","",16712190),
(388,"|cffffd100Tip:|r Clicking an item name that appears bracketed in chat will tell you more about the item.
","","","","","","","","","","","","","","","",16712190),
(390,"|cffffd100Tip:|r It's considered polite to talk to someone before inviting them into a group, or opening a trade window.","","","","","","","","","","","","","","","",16712190),
(392,"|cffffd100Tip:|r Your items do not suffer durability damage when you are killed by an enemy player.","","","","","","","","","","","","","","","",16712190),
(393,"|cffffd100Tip:|r <Shift>click on a quest in your quest log to toggle quest tracking for that quest.
","","","","","","","","","","","","","","","",16712190),
(397,"|cffffd100Tip:|r The auction houses in each of your faction's major cities are linked together. 
","","","","","","","","","","","","","","","",16712190),
(394,"|cffffd100Tip:|r Nearby questgivers that are awaiting your return are shown as a yellow question mark on your mini-map.
","","","","","","","","","","","","","","","",16712190),
(399,"|cffffd100Tip:|r Quests completed at maximum level award money instead of experience.
","","","","","","","","","","","","","","","",16712190),
(403,"|cffffd100Tip:|r <Shift>-B will open all your bags at once.","","","","","","","","","","","","","","","",16712190),
(404,"|cffffd100Tip:|r When interacting with other players a little kindness goes a long way! 
","","","","","","","","","","","","","","","",16712190),
(407,"|cffffd100Tip:|r Bring your friends to Azeroth, but don't forget to go outside Azeroth with them as well.
","","","","","","","","","","","","","","","",16712190),
(409,"|cffffd100Tip:|r If you keep an empty mailbox, the mail icon will let you know when you have new mail waiting!
","","","","","","","","","","","","","","","",16712190),
(410,"|cffffd100Tip:|r Never give another player your account information.","","","","","","","","","","","","","","","",16712190),
(419,"|cffffd100Tip:|r When a player not in your group damages a monster before you do, it will display a gray health bar and you will get no loot or experience from killing it.
","","","","","","","","","","","","","","","",16712190),
(420,"|cffffd100Tip:|r You can see the spell that your current target is casting by turning on the 'Cast Bars' options in the combat interface options tab.
","","","","","","","","","","","","","","","",16712190),
(422,"|cffffd100Tip:|r You can access the map either by clicking the map button in the upper right of the mini-map or by hitting the 'M' key. The map can help you locate where you need to go to complete a quest.
","","","","","","","","","","","","","","","",16712190),
(423,"|cffffd100Tip:|r Many high level dungeons have a heroic mode setting. Heroic mode dungeons are tuned for higher level players and have improved loot.
","","","","","","","","","","","","","","","",16712190),
(424,"|cffffd100Tip:|r Spend your honor points for powerful rewards at the Champion's Hall (Alliance) or Hall of Legends (Horde).
","","","","","","","","","","","","","","","",16712190),
(425,"|cffffd100Tip:|r The honor points you earn each day become available immediately. Check the PvP interface to see how many points you have to spend.
","","","","","","","","","","","","","","","",16712190),
(412,"|cffffd100Tip:|r You can turn these tips off in the Interface menu.","","","","","","","","","","","","","","","",16712190),
(426,"|cffffd100Tip:|r Dungeon meeting stones can be used to summon absent party members.  It requires two players at the stone to do a summoning.
","","","","","","","","","","","","","","","",16712190),
(427,"|cffffd100Tip:|r The Parental Controls section of the Account Management site offers tools to help you manage your play time.
","","","","","","","","","","","","","","","",16712190),
(428,"|cffffd100Tip:|r Quest items that are in the bank cannot be used to complete quests.
","","","","","","","","","","","","","","","",16712190),
(429,"|cffffd100Tip:|r A quest marked as (Failed) in the quest log can be reacquired from the quest giver.
","","","","","","","","","","","","","","","",16712190),
(430,"|cffffd100Tip:|r The number next to the quest name in your log is how many other party members are on that quest.
","","","","","","","","","","","","","","","",16712190),
(431,"|cffffd100Tip:|r You cannot advance quests other than (Raid) quests while you are in a raid group.
","","","","","","","","","","","","","","","",16712190),
(432,"|cffffd100Tip:|r You cannot cancel your bids in the auction house so bid carefully.
","","","","","","","","","","","","","","","",16712190),
(433,"|cffffd100Tip:|r To enter a chat channel, type /join [channel name] and /leave [channel name] to exit.
","","","","","","","","","","","","","","","",16712190),
(434,"|cffffd100Tip:|r Mail will be kept for a maximum of 30 days before it disappears.
","","","","","","","","","","","","","","","",16712190),
(435,"|cffffd100Tip:|r Once you get a key, they can be found in a special key ring bag that is to the left of your bags.
","","","","","","","","","","","","","","","",16712190),
(437,"|cffffd100Tip:|r City Guards will often give you directions to other locations of note in the city.
","","","","","","","","","","","","","","","",16712190),
(438,"|cffffd100Tip:|r You can repurchase items you have recently sold to a vendor from the buyback tab.
","","","","","","","","","","","","","","","",16712190),
(439,"|cffffd100Tip:|r A group leader can reset their instances or change dungeon difficulty by Right Clicking on their character portrait.","","","","","","","","","","","","","","","",16712190),
(440,"|cffffd100Tip:|r You can always get a new hearthstone from any innkeeper.
","","","","","","","","","","","","","","","",16712190),
(441,"|cffffd100Tip:|r You can open a small map of the current zone either with Shift-M or as an option from the world map.","","","","","","","","","","","","","","","",16712190),
(442,"|cffffd100Tip:|r Players cannot dodge, parry, or block attacks that come from behind them.","","","","","","","","","","","","","","","",16712190),
(444,"|cffffd100Tip:|r You can replace a gem that is already socketed into your item by dropping a new gem on top of it in the socketing interface.","","","","","","","","","","","","","","","",16712190),
(445,"|cffffd100Tip:|r If you Right Click on a name in the combat log a list of options will appear.
","","","","","","","","","","","","","","","",16712190),
(446,"|cffffd100Tip:|r You can only have one Battle Elixir and one Guardian Elixir on you at a time.","","","","","","","","","","","","","","","",16712190),
(447,"|cffffd100Tip:|r When you are mousing over an item, you can hold down the shift key and it will also show you the item of that type you have equipped.
","","","","","","","","","","","","","","","",16712190),
(448,"|cffffd100Tip:|r You can have up to 10 characters per realm, with a maximum of 50 characters total across all realms. ","","","","","","","","","","","","","","","",16712190),
(449,"|cffffd100Tip:|r You can reuse text that you have recently typed. Press Enter, then use <Alt> + Up Arrow to scroll through your previous messages. ","","","","","","","","","","","","","","","",16712190),
(464,"|cffffd100Tip:|r You can use the Calendar to see upcoming holidays or plan events with your guild or friends.","","","","","","","","","","","","","","","",16712190),
(465,"|cffffd100Tip:|r You can Right Click on the Clock to set an alarm or display a stopwatch.","","","","","","","","","","","","","","","",16712190),
(450,"|cffffd100Tip:|r Your mounts and non-combat companions can be accessed from the Pets tab on your character page.","","","","","","","","","","","","","","","",16712190),
(451,"|cffffd100Tip:|r Currencies, such as the ones collected from Battlegrounds or Heroic dungeons, are stored in the Currency Page.","","","","","","","","","","","","","","","",16712190),
(452,"|cffffd100Tip:|r Some vehicles can carry multiple passengers.","","","","","","","","","","","","","","","",16712190),
(466,"|cffffd100Tip:|r Characters with the Inscription profession can inscribe glyphs to improve some of your favorite spells and abilities.","","","","","","","","","","","","","","","",16712190),
(467,"|cffffd100Tip:|r If you're looking for something different to do, try to complete some of the more unusual Achievements.","","","","","","","","","","","","","","","",16712190),
(468,"|cffffd100Tip:|r You can link quests, items, spells and achievements to chat messages by Shift-Left Clicking in your log.","","","","","","","","","","","","","","","",16712190),
(469,"|cffffd100Tip:|r You can track progress on current quests or achievements.","","","","","","","","","","","","","","","",16712190),
(453,"|cffffd100Tip:|r Flying mounts can be used in Outland once you attain the appropriate skill level of Riding and in Northrend after training Cold Weather Flying.","","","","","","","","","","","","","","","",16712190),
(454,"|cffffd100Tip:|r Completing some Achievements may grant unique non-combat items as rewards.","","","","","","","","","","","","","","","",16712190),
(455,"|cffffd100Tip:|r Items with bonus spell power improve both spell damage and healing.","","","","","","","","","","","","","","","",16712190),
(456,"|cffffd100Tip:|r Dealing damage, healing or casting spells can generate Threat against a creature.  Most creatures will attack the player with the highest Threat against them.","","","","","","","","","","","","","","","",16712190),
(457,"|cffffd100Tip:|r Creatures four levels above you can hit you with a Crushing Blow for extra damage.","","","","","","","","","","","","","","","",16712190),
(458,"|cffffd100Tip:|r Statistics track some interesting numbers related to your character, such as total food eaten and highest critical hit.","","","","","","","","","","","","","","","",16712190),
(459,"|cffffd100Tip:|r You can have your hair cut, colored or styled in a Barber Shop located in many cities.","","","","","","","","","","","","","","","",16712190),
(460,"|cffffd100Tip:|r Recruiting a friend to World of Warcraft can provide rewards for both of you.","","","","","","","","","","","","","","","",16712190),
(462,"|cffffd100Tip:|r You can set a Focus Frame by Right Clicking on a unit frame.  You can unlock a Focus Frame to move it anywhere around your screen.","","","","","","","","","","","","","","","",16712190),
(461,"|cffffd100Tip:|r You can compare your Achievements to those of another player.","","","","","","","","","","","","","","","",16712190),
(463,"|cffffd100Tip:|r You can set an option to cast spells on yourself or on your Focus target by looking in Interface Options, Combat.","","","","","","","","","","","","","","","",16712190),
(471,"|cffffd100Tip:|r A sanctuary is a town in which Player vs. Player combat is prohibited, such as Shattrath and Dalaran.","","","","","","","","","","","","","","","",16712190),
(472,"|cffffd100Tip:|r Improving your reputation with a faction can grant access to new quests or items.","","","","","","","","","","","","","","","",16712190),
(473,"|cffffd100Tip:|r The Calendar can tell you when raids reset.","","","","","","","","","","","","","","","",16712190),
(474,"|cffffd100Tip:|r Creatures cannot make critical hits with spells, but players can.","","","","","","","","","","","","","","","",16712190),
(475,"|cffffd100Tip:|r Creatures can dodge attacks from behind, but players cannot.  Neither creatures nor players can parry attacks from behind.","","","","","","","","","","","","","","","",16712190),
(479,"|cffffd100Tip:|r You can activate the Equipment Manager feature under Interface Controls to let you quickly swap to sets of gear that you find yourself using often.","","","","","","","","","","","","","","","",16712190),
(480,"|cffffd100Tip:|r Once you reach level 40, your class trainer can instruct you in Dual Talent Specialization. This feature allows you to swap out your talents, glyphs and action bars between two commonly used configurations.","","","","","","","","","","","","","","","",16712190),
(481,"|cffffd100Tip:|r You can enable Arena Enemy Frames to display the unit frames of the enemy team when in Arena matches.","","","","","","","","","","","","","","","",16712190),
(482,"|cffffd100Tip:|r Under Interface Display is an option to Preview Talent Changes. If you select this option, you can try out various ways to spend talent points before saving the changes.","","","","","","","","","","","","","","","",16712190),
(483,"|cffffd100Tip:|r If you press <Alt> while your cursor is over an item slot on your character, you can produce a list of items that can be equipped in that slot.","","","","","","","","","","","","","","","",16712190),
(489,"|cffffd100Tip:|r If an enemy cast bar has a shield icon around it, that means you cannot interrupt the spell with your abilities.","","","","","","","","","","","","","","","",16712190),
(490,"|cffffd100Tip:|r Your guild can schedule Guild Events using the in-game calendar. You choose to sign up for these events rather than waiting for an invitation.","","","","","","","","","","","","","","","",16712190),
(491,"|cffffd100Tip:|r When the autocomplete feature suggests character names, you can use Tab or <Shift> Tab to select a name from the list.","","","","","","","","","","","","","","","",16712190),
(500,"|cffffd100Tip:|r Always verify that you are accessing an official Blizzard website whenever submitting account information.","","","","","","","","","","","","","","","",16712190),
(501,"|cffffd100Tip:|r Emails from Blizzard will always end in either blizzard.com or battle.net.","","","","","","","","","","","","","","","",16712190),
(502,"|cffffd100Tip:|r You can download free programs to regularly scan your machine for malicious software including keyloggers.","","","","","","","","","","","","","","","",16712190),
(503,"|cffffd100Tip:|r For additional features and functionality, merge your World of Warcraft account into a Blizzard Battle.net account.","","","","","","","","","","","","","","","",16712190),
(504,"|cffffd100Tip:|r Using a Blizzard Authenticator or Battle.net Mobile Authenticator will help protect your World of Warcraft account from theft.","","","","","","","","","","","","","","","",16712190),
(505,"|cffffd100Tip:|r To avoid account theft, do not login to World of Warcraft on a computer that is shared with the general public. These machines can be infected with malicious software.","","","","","","","","","","","","","","","",16712190),
(506,"|cffffd100Tip:|r Be wary of unofficial sites advertising paid World of Warcraft services. These may contain malicious software that can lead to account theft.","","","","","","","","","","","","","","","",16712190),
(507,"|cffffd100Tip:|r To avoid account theft, do not use browser add-ons or features that allow scripts to be run in the background.","","","","","","","","","","","","","","","",16712190),
(508,"|cffffd100Tip:|r Quest locations and questgivers can be seen on your map.","","","","","","","","","","","","","","","",16712190),
(509,"|cffffd100Tip:|r You can earn rewards for running random dungeons using the Dungeon Finder tool. You can go with a group of friends or use Dungeon Finder to find a dungeon group.","","","","","","","","","","","","","","","",16712190); 
