DROP TABLE IF EXISTS `TransportPhysics`; 
CREATE TABLE `TransportPhysics` ( `ID` INT NOT NULL DEFAULT '0', `WaveAmp` FLOAT NOT NULL DEFAULT '0', `WaveTimeScale` FLOAT NOT NULL DEFAULT '0', `RollAmp` FLOAT NOT NULL DEFAULT '0', `RollTimeScale` FLOAT NOT NULL DEFAULT '0', `PitchAmp` FLOAT NOT NULL DEFAULT '0', `PitchTimeScale` FLOAT NOT NULL DEFAULT '0', `MaxBank` FLOAT NOT NULL DEFAULT '0', `MaxBankTurnSpeed` FLOAT NOT NULL DEFAULT '0', `SpeedDampThresh` FLOAT NOT NULL DEFAULT '0', `SpeedDamp` FLOAT NOT NULL DEFAULT '0', PRIMARY KEY (`ID`)) ENGINE=InnoDB DEFAULT CHARSET=utf8; 
